@(".\node_modules", ".\pnpm-lock.yaml") + @(".\apps", ".\packages" | ForEach-Object { Get-ChildItem $_ -Directory | ForEach-Object { "$($_.FullName)\node_modules" } }) | ForEach-Object { if (Test-Path $_) { Write-Host "Removing: $_"; try { Remove-Item $_ -Recurse -Force -ErrorAction Stop } catch { Write-Warning "Failed to remove $_: $($_.Exception.Message)" } } }
